mapper.select:
select

from t_enshrine


    and p_id = #{pId,jdbcType=VARCHAR}


    and h_id = #{hId,jdbcType=VARCHAR}


    and t_type = #{tType,jdbcType=VARCHAR}

select e.t_id as tId, e.t_type as tType, s.t_store_name as tStoreName, s.t_explain as tExplain, s.t_phone as tPhone, s.t_site as tSite , a.t_headline as tHeadline, a.t_money as tMoney , a.t_site as asite, a.t_id as aId, s.t_id as sId, s.t_picture as tPicture, a.t_img as tImg, a.t_start_time as tStartTime from t_enshrine e left join t_activity a on e.h_id=a.t_id left join t_store s on e.h_id = s.t_id
and e.p_id = #{pId,jdbcType=VARCHAR}
and t_type = #{tType,jdbcType=VARCHAR}

mapper.update:
update t_enshrine
set h_id = #{hId,jdbcType=VARCHAR},
  p_id = #{pId,jdbcType=VARCHAR},
  t_time = #{tTime,jdbcType=VARCHAR},
  t_type = #{tType,jdbcType=VARCHAR}
where t_id = #{tId,jdbcType=VARCHAR}

mapper.select:
select

from t_enshrine
where h_id = #{hId,jdbcType=VARCHAR} and p_id = #{pId,jdbcType=VARCHAR} and t_type = #{tType,jdbcType=VARCHAR}
